import React, { useEffect, useRef, useState } from "react";
import '../notes/MyNotes.css'

function MyNotes() {
  const [files,setFiles] = useState();
  const dropContainerRef = useRef(null);
  const fileInputRef = useRef(null);
  
  useEffect(()=>{
    const dropContainer = dropContainerRef.current;

    const handleDragOver = (e) =>{
      e.preventDefault();
    }

    const handleDragEnter = ()=>{
      dropContainer.classList.add("drag-active")
    }

    const handleDragLeave = ()=>{
      dropContainer.classList.remove("drag-active")
    }

    const handleDrop = (e) =>{
      e.preventDefault()
      dropContainer.classList.remove('drag-active');

      const droppedFiles = e.dataTransfer.items;
      let allFiles = [];

      for (let i = 0; i < droppedFiles.length; i++) {
        if (droppedFiles[i].webkitGetAsEntry().isDirectory) {
          readDirectory(droppedFiles[i].webkitGetAsEntry(), allFiles);
        } else {
          allFiles.push(droppedFiles[i].getAsFile());
        }
      }

      setFiles([...files,...allFiles])
    }

    const readDirectory = (directory,allFiles) =>{
      const reader = directory.createReader();
      reader.readEntries((entries)=>{
        for(let entry of entries)
          {
            if(entry.isDirectory){
              readDirectory(entry,allFiles)
            }
            else{
              entry.files((file)=>{
                allFiles.push(file)
              })
            }
          }
      })
    }

    dropContainer.addEventListener("dragover", handleDragOver, false);
    dropContainer.addEventListener("dragenter", handleDragEnter, false);
    dropContainer.addEventListener("dragleave", handleDragLeave, false);
    dropContainer.addEventListener("drop", handleDrop, false);

    return ()=>{
      dropContainer.removeEventListener("dragover", handleDragOver, false);
      dropContainer.removeEventListener("dragenter", handleDragEnter, false);
      dropContainer.removeEventListener("dragleave", handleDragLeave, false);
      dropContainer.removeEventListener("drop", handleDrop, false);
    }
  })

  const renderFiles = (fileList) => {
    return fileList.map((file, index) => (
      <div key={index} className="file-tile">
        <a href={URL.createObjectURL(file)} target="_blank" rel="noopener noreferrer">
          {file.name}
        </a>
      </div>
    ));
  };

  return (
    <div className="notes">
      <div className="n-upload">
        <label for="images" class="drop-container" id="dropcontainer">
          <span class="drop-title">Drop files here</span>
          or
          <input 
          type="file" 
          id="images"
          webkitdirectory="true" 
          accept="image/*" 
          multiple 
          required />
        </label>
      </div>
      <div className="file-list">
        {renderFiles(files)}
      </div>
    </div>
  );
}

export default MyNotes;
