  const canvasRef = useRef<HTMLCanvasElement>(null)
  
  useEffect(()=>{
    let isDrawing = false;
    let lastx = 0;
    let lasty = 0;

    const startDrawing = (e:{offsetX:Number,offsetY:number})
  })
  
  return (
    <div>
      <canvas 
      ref={canvasRef}
      width={600}
      height={400}
      />
    </div>
  )
}

export default DrawBoard
