import React, { useEffect, useRef, useState } from 'react'
import '../draw/DrawBoard.css'
import { ReactSketchCanvas } from 'react-sketch-canvas';

const styles = {
  border: '0.0625rem solid #9c9c9c',
  borderRadius: '0.25rem',
};

function DrawBoard() {
  

  return (
    <div>
      <ReactSketchCanvas
      style={styles}
      width="600"
      height="400"
      strokeWidth={4}
      strokeColor="red"
    />
    </div>
  )
}

export default DrawBoard
