import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css'
import axios from 'axios';

function Login({logged,setLog}) {
    const [data,setData] = useState({})
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [validpass, setValidpass] = useState('');
    const navigate = useNavigate();

    const baseURL = `http://localhost:4000/route/login`

    const handleLogin = (event) =>{
        event.preventDefault();

        axios.post("http://localhost:4000/route/login",uamesern)
        .then(response =>{
            setData(response.data)
        })
        console.log(data)
        if(password == validpass)
            {
                setLog(true);
                navigate('/Search');
            }
        else{
            navigate('/Login');
        }
    }

    const handleSignUp = () => {
      navigate('/SignUp');
    };
  return (
    <div>
      <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
            <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="form-buttons">
                    <button type="submit" className="login-button">Login</button>
                    <button type="button" className="signup-button" onClick={handleSignUp}>Sign Up</button>
                </div>
            </form>
        </div>
    </div>
  )
}

export default Login
