  }

  return (
    <div className='draw'>
      <ReactSketchCanvas
      style={styles}
      ref={canvasRef}
      strokeWidth={size}
      strokeColor={color}

    />
    <div className="tool-bar">
      <HexColorPicker 
      color={color}
      onChange={setColor}
      />
      <div className="size">
        <h3 key={size}>{size}</h3>
        <button onClick={increment}>+</button>
        <button onClick={decrement}>-</button>
      </div>
    </div>
    </div>

  )
}

export default DrawBoard
