import React, { useState } from 'react';
import File from './File';
import FileUploader from './FileUploader';

const Folder = ({ folder, addFileOrFolder }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleOpen = () => {
    setIsOpen(!isOpen);
  };

  const addItem = (newItem) => {
    addFileOrFolder(folder.id, newItem);
  };

  return (
    <div className="folder">
      <div onClick={toggleOpen} className="folder-header">
        {folder.isFolder ? 'üìÅ' : 'üìÑ'} {folder.name}
      </div>
      {isOpen && (
        <div className="folder-contents">
          {folder.children && folder.children.map((item) =>
            item.isFolder ? (
              <Folder key={item.id} folder={item} addFileOrFolder={addFileOrFolder} />
            ) : (
              <File key={item.id} file={item} />
            )
          )}
          {folder.isFolder && <FileUploader onUpload={addItem} />}
        </div>
      )}
    </div>
  );
};

export default Folder;
