import React, { useEffect, useState } from 'react';
import {useNavigate} from 'react-router-dom'
import './Profile.css';
import default_profile from '../../assets/default_profile.png'


const Profile = ({logged,setLog,username,baseUrl}) => {
    const[name,setName] = useState("");
    const[email,setEmail] = useState("");
    const[institute,setInstitute] = useState("");
    const navigate = useNavigate()
    
    useEffect(() => {
        console.log('hehe'+baseUrl)
        const fetchProfile = async()=>{

            const response = await fetch('http://localhost:4000/route/profile',{
                method:'POST',
                mode:'cors',
                headers:{
                    'Content-Type':'application/json'
                },
                body:JSON.stringify({
                    username:username
                })
            })
            const data = await response.json();
            setName(data.name)
            set
            console.log(data);
        }
        fetchProfile();
        })
    

    const handleLogout=()=>{
        setLog(false)
        navigate('Home')
    }
    
    return(
        <div className="profile">
            <div className="pf-header">
                <div className="pf-left">
                    <img src={default_profile} alt="" />
                </div>
                <div className="pf-right">
                    <div className="username"> {username}</div>
                    <div className="text"> Name: {name}</div>
                    <div className="text"> Email : {email}</div>
                    <div className="text"> Institute: {institute}</div>
                </div>
            </div>
            <div className="logout">
                <button onClick={handleLogout}> Logout </button>
            </div>
        </div>
    )
};

export default Profile;
