import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [name, setName] = useState('John Doe');
    const [email, setEmail] = useState('john.doe@example.com');
    const [username, setUsername] = useState('johndoe');
    const [password, setPassword] = useState('password123');
    const [institute, setInstitute] = useState('Institute1');
    const [isEditing, setIsEditing] = useState(false);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
        setImagePreview(URL.createObjectURL(file));
    };

    const handleSave = (e) => {
        e.preventDefault();
        setIsEditing(false);
        // Add save logic here, e.g., send data to the server
        console.log('Profile saved:', {
            image,
            name,
            email,
            username,
            password,
            institute,
        });
    };

    const handleEdit = () => {
        setIsEditing(true);
    };

    return (
        <div className="profile-container">
            <h2>Profile</h2>
            <form onSubmit={handleSave}>
                <div className="form-group">
                    <label htmlFor="image">Profile Image</label>
                    {isEditing ? (
                        <>
                            <input
                                type="file"
                                id="image"
                                name="image"
                                accept="image/*"
                                onChange={handleImageChange}
                            />
                            {imagePreview && <img src={imagePreview} alt="Image Preview" className="image-preview" />}
                        </>
                    ) : (
                        <img src={imagePreview || 'default-profile.png'} alt="Profile" className="image-preview" />
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    {isEditing ? (
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    ) : (
                        <p>{name}</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    {isEditing ? (
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    ) : (
                        <p>{email}</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    {isEditing ? (
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                    ) : (
                        <p>{username}</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    {isEditing ? (
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    ) : (
                        <p>••••••••</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="institute">Institute</label>
                    {isEditing ? (
                        <select
                            id="institute"
                            name="institute"
                            value={institute}
                            onChange={(e) => setInstitute(e.target.value)}
                            required
                        >
                            <option value="">Select your institute</option>
                            <option value="Institute1">Institute1</option>
                            <option value="Institute2">Institute2</option>
                            <option value="Institute3">Institute3</option>
                        </select>
                    ) : (
                        <p>{institute}</p>
                    )}
                </div>
                {isEditing ? (
                    <button type="submit" className="save-button">Save</button>
                ) : (
                    <button type="button" className="edit-button" onClick={handleEdit}>Edit</button>
                )}
            </form>
        </div>
    );
};

export default Profile;
