import React, { Children, useState } from 'react'
import Folder from './Folder'
import FileUploader from './FileUploader'

const initialStructure ={
  id:'root',
  name:'root',
  isFolder: true,
  children:[]
}

function MyNotes() {
  const [fileStructure, setFileStructure] = useState(initialStructure);

  const addFileOrFolder = (parentID,newItem)=>{
    const updateStructure = (structure) =>{
      if(structure.id === parentId){
        structure.children.push(newItem);
      }
      else if(structure.children){
        structure.children = structure.children.map(updateStructure);
      }
      return structure;
    }
  }
  return (
    <div>MyNotes</div>
  )
}

export default MyNotes
