import React, { useState } from 'react';
import './SignUp.css';
import { useNavigate } from 'react-router-dom';

const SignUp = ({logged,setLog,baseURL}) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [institute, setInstitute] = useState('');
    const [nerr, setNerr] = useState('');
    

    const navigate = useNavigate();


    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const response = await fetch(baseURL+'signup',{
            method:'POST',
            headers:{
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                name:name,
                email:email,
                username:username,
                password:password,
                institute:institute,
            })
        })

        const data = response.json();
        if(data.success){
                setLog(true);
                navigate('/Login');
            }
        else{
            if(data.message==='username present'){
                setNerr("*this username is already taken")
            }
            else
            {
                alert("Server Error");
            }
        }

    };

    return (
        <div className="signup-container">
            <h2>Sign Up</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div className="signup-error">{nerr}</div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="institute">Institute</label>
                    <select
                        id="institute"
                        name="institute"
                        value={institute}
                        onChange={(e) => setInstitute(e.target.value)}
                        required
                    >
                        <option value="">Select your institute</option>
                        <option value="Lovely Professional University">Lovely Professional University</option>
                        <option value="Delhi University">Delhi University</option>
                        <option value="Chandigarh University">Chandigarh University</option>
                    </select>
                </div>
                <button type="submit" className="signup-button">Sign Up</button>
            </form>
        </div>
    );
};

export default SignUp;
